export default {
  name: 'resource-tree',
  props: {
    roleId: Number
  },
  data: function data() {
    return {
      split1: 0.4,
      resourceDetail: {
        orderStr: "",
        title: "",
        url: "",
        level: "",
        descn: ""
      },
      treedata: [],
      allCheckedNodeId: []
    };
  },
  methods: {
    checkChange: function checkChange(data) {
      this.allCheckedNodeId = [];
      this.getAllCheckedNodeId(this.treedata);
      console.log(this.allCheckedNodeId);
    },
    selectChange: function selectChange(data) {
      console.log(data);
      this.resourceDetail = data[0];
    },
    //初始化tree
    initTree: function initTree() {
      console.log("init...");
    },
    //获取后台节点
    findNodes: function findNodes(roleid) {
      var _this = this;

      console.log(roleid);
      var data = {
        "roleid": roleid,
        "status": "1"
      };
      var token = "123";
      this.$axios.get("/admin-server/permission/findPermissionTreeData", {
        params: {
          data: data,
          token: token
        }
      }).then(function (res) {
        if (res.data.code == 200) {
          _this.treedata = res.data.data;
        } else {
          _this.$Notice.error({
            title: "错误提示",
            duration: 5,
            desc: res + "<br/>无法获取后台数据！"
          });
        }
      }).catch(function (error) {
        _this.$Notice.error({
          title: "错误提示",
          duration: 5,
          desc: error + "<br/>无法获取后台数据！"
        });
      });
    },
    //获取所有勾选节点的id，包括父节点  
    getAllCheckedNodeId: function getAllCheckedNodeId(parent) {
      var _this2 = this;

      parent.forEach(function (item) {
        if (item.indeterminate || item.checked) {
          _this2.allCheckedNodeId.push(item.id);

          if (item.children) {
            _this2.allCheckedNodeId.concat(_this2.getAllCheckedNodeId(item.children));
          }
        }
      });
    }
  },
  mounted: function mounted() {
    this.initTree();
  }
};
